#include <iostream>
#include <vector>
using namespace std;

double srednia(vector<double> T, int N) {
	double suma = 0;
	for (int i = 0; i < N; i++)
	{
		suma = suma + T[i];
	}

	return suma / (double) N;
}

int main()
{
	int n;
	double a, suma;
	vector<double> T;

	cout << "Ile liczb: ";
	cin >> n;
	if (n > 0)
	{
		for (int i = 0; i < n; i++) {
			cin >> a;
			T.push_back(a);
		}
	}
	else
		cout << "Niepoprawne dane";

	cout << "Srednia arytmetyczna: " << srednia(T, n);

	return 0;
}
